import urllib.request
import re

url = input("Enter the URL : \n").strip()
response = urllib.request.urlopen(url).read()

# store hyperlinks
hlinks = []
#stores text
text = []
#stores images
images = []


#extracts all links
links = re.findall(b'href="(https?://.*?)"', response)
for link in links:
    hlinks.append(link.decode())

#extracts all text in p html tag
p = re.findall(b'<p>([^<">]+)', response)
for para in p:
    text.append(para.decode())

#extracts all images src links
img_tags = re.findall(b'<img[^>]+src="([^">]+)"', response)
for image in img_tags:
    images.append(image.decode())

print("HyperLinks : ")
print(hlinks)

print("\nText : ")
print(text)

print("\nImage src links : ")
print(images)
